//NFC Functionality



var app = {
		handleMifareTag: function(tag) {
			alert(tag);
		},
	    initialize: function () {
	    	
	        this.bind();
	    },
	    bind: function () {
	        document.addEventListener('deviceready', this.deviceready, true);
	    },
	    deviceready: function () {
	    
	        function failure(reason) {
	            navigator.notification.alert("Failure : " + reason, function() {}, "pHmHealth");
	            Ext.getCmp('loginForm').setMasked(false);
	        }

	        nfc.addNdefListener(
	            app.onNdef,
	            function() {
	                console.log("Listening for Patient tag in.");
	            },
	            failure
	        );
	        

	        if (device.platform == "Android") {

	            // Android reads non-NDEF tag. BlackBerry and Windows don't.
	            nfc.addTagDiscoveredListener(
	                app.onNfc,
	                function() {
	                    console.log("Listening for non-NDEF tags.");
	                },
	                failure
	            );

	            // Android launches the app when tags with mime type text/pg are scanned
	            // because of an intent in AndroidManifest.xml.
	            // phonegap-nfc fires an ndef-mime event (as opposed to an ndef event)
	            // the code reuses the same onNfc handler
	            nfc.addMimeTypeListener(
	                'text/pg',
	                app.onNdef,
	                function() {
	                    console.log("Listening for NDEF mime tags with type text/pg.");
	                },
	                failure
	            );
	        }

	    },
	    onNfc: function (nfcEvent) {
	        navigator.notification.alert("This card is not ndef formatted. ", function() {}, "pHmHealth");
	    },
       onNdef: function (nfcEvent) {
       
    	   
    	   if(tagFlag==0)
    	  {	   
	        //Patient tag-in

	        var tag = nfcEvent.tag,
	        records = tag.ndefMessage || [];
	 	        
	        var ndefRecord = "";
	       
	        ndefRecord = nfc.decodePayload(records[0]);
	        
	        if (ndefRecord=="")
	       {
	        	navigator.notification.alert("No patient-id found on card. ", function() {}, "pHmHealth");	
	       }
	       
	        else
	       { 	
            //There exists patient id on card.
	        	
	        id = ndefRecord;
	        
	        var flag=0;
	        
	          if(id==tplPatientId)
	    		  
	    	  flag=1;	  
            
	        if(flag==1)
	        {        
            	//Patient is authorized, thus start encounter. At this point HCP navigates to patient panel page.
	        	app.startEncounter(id);
	       	}
	        else
	        {
	        	//Patient is Un-Authorized.
	        	var pt = patientDetails[tplPatientId];
	        	navigator.notification.alert("Un-Authorized Patient.", function() {}, "pHmHealth");
	        }
    	  }  
	    }
    	   else
    	  {
    		//Patient tag-in is finished. Performing functionality for tag-out.
    		var tag = nfcEvent.tag,
   	        records = tag.ndefMessage || [];

   	        var ndefRecord = "";
   	       
   	        ndefRecord = nfc.decodePayload(records[0]);
   	        
   	     if (ndefRecord=="")
	       {
	        	navigator.notification.alert("No patient-id found on card. ", function() {}, "pHmHealth");	
	       }
	       
	        else
	       { 	
   	        
   	        if(id == ndefRecord)
   	        	{
   	        	 //Swiped the correct card using which the tag-in procedure was performed earlier. Tag-out successful. Start end-encounter.
   	        	 app.endEncounter(id);
   	        	}
   	        else
   	        	{
   	        	navigator.notification.alert("Please tag out with the card used for tag-in.", function() {}, "pHmHealth");
   	        	}
    		   
    	  }
    	 }
       },
	   setPatient : function(id)
	   {
		   //Setting the demographics data of the patient in the Demographics page of the application.
		   var patient=patientDetails[id];
		   
		   if(patient!=null)
			   {
			    Ext.getCmp('firstName').setHtml(firstName);
      	    	
      	    	Ext.getCmp('name').setHtml(patient.firstname+" "+patient.lastname);
      	    	Ext.getCmp('phone').setHtml(patient.phone);
      	    	Ext.getCmp('address').setValue(patient.streetaddress+",\n"+patient.city+",\n"+patient.state+" "+patient.zipcode+","+patient.Country);
      	    	Ext.getCmp('birthDate').setHtml(patient.dateOfBirth);
      	    	Ext.getCmp('gender').setHtml(patient.gender);
      	    	Ext.getCmp('maritalStatus').setHtml(patient.maritalstatus);
      	    	
      	    	visitId=uniqueId; 
			   }
	   },
	  startEncounter : function(id)
	  {
		  console.log(doctorId+"--"+id+"--"+uniqueId);
		  Ext.Ajax.request ({ 
		        
		        url : phmHealth.URLs.startEncounterURL,
		        timeout : 180000,
		        method : 'POST',
		        type:'ajax',
		        
		        jsonData : {
		         providerid: doctorId.toString(),
		         patientid : id.toString(),
		         scheduleid : uniqueId.toString()
		        },
		                 success: function(response) {
		                	 var jsonResponse = JSON.parse(response.responseText);
		                	 
		                	 if(tagPage==0)
		      	               Ext.getCmp('patientPanel').destroy();
		      	       		else
		      	       		   Ext.getCmp('patientdetailPanel').destroy();
		      	       		 
		      	       		var paneltab = Ext.create('MyApp.view.MainTabPanel');
		      	       		
		                  	Ext.Viewport.add(paneltab); 	                  			                	
		                	
		                  	app.setPatient(uniqueId);
		                  	
		                	tagFlag=1;	    
		                  	
		                	 
		                 },
		                 failure: function(response) {
		                   navigator.notification.alert("Server error!! Please try again.", function() {}, "pHmHealth");
		                 }
		                    
		             }); 
	  },
	  endEncounter : function(id)
	  {
		  console.log(doctorId+"--"+id+"--"+visitId+"--"+duratn);
		  
		  if(duratn=="")
				navigator.notification.alert("Please press END ENCOUNTER button and then tag out.", function() {}, "pHmHealth");
		  else
			  {
		  Ext.Ajax.request ({ 
		        
		        url : phmHealth.URLs.endEncounterURL,
		        timeout : 180000,
		        method : 'POST',
		        type:'ajax',
		        
		        jsonData : {
		         providerid: doctorId.toString(),
		         patientid : id.toString(),
		         visitid : visitId.toString(),
		         duration : duratn
		        },
		                 success: function(response) {
		                	 var jsonResponse = JSON.parse(response.responseText);
		                	console.log("Response : "+Ext.JSON.encode(jsonResponse));	
		                	window.location="app.html";
		                	 
		                 },
		                 failure: function(response) {
		                   console.log(response.responseText);
		                   navigator.notification.alert("Server error!! Please try again.", function() {}, "pHmHealth");
		                 }
		                    
		             }); 
	  }		
	}
}
